sp_widget.client_script:
function($rootScope, $scope, spUtil) {
	var c = this;
	c.data.selectedItem = {};

	c.selectCategory = function(selectedCategory){		
		console.log(selectedCategory);
		c.data.selectedItem = selectedCategory;
		c.server.get({
			categoryId : selectedCategory.sys_id,
			action : 'getNoteList'
		}).then(function(r){
			$rootScope.categoryName = selectedCategory.category_name;
			$rootScope.categoryId = selectedCategory.sys_id;
			$rootScope.$emit('showNoteList', r.data.noteList);
			// true: Show Note list
			$rootScope.$emit('toggleNoteList', true);
			// false: Hide Note details
			$rootScope.$emit('toggleNoteDetail', false);
			// false: Hide Category details
			$rootScope.$emit('toggleCategoryDetail', false);
		});		
		// Set widget options
		if (!c.options.category){
			c.options.category = 'My Category';
		}
	};

	c.newCategory = function (){
		c.server.get({
			action : 'newCategory'
		}).then(function(r){
			c.data.categoryList.unshift(r.data.newCategory);
			$rootScope.$emit('showCategoryDetail', r.data.newCategory);
			// Emply Note Detail
			$rootScope.$emit('resetBody', "");
		});
	};

	c.editCategory = function editCategory(selectedCategory){
		c.data.selectedItem = selectedCategory;
		c.server.get({
			cateSysId : selectedCategory.sys_id,
			action : 'getCategory'
		}).then(function(r){
			// Pass category detail
			$rootScope.$emit('setDetails', r.data.categoryObj);

			// true: Show Note list
			$rootScope.$emit('toggleNoteList', false);
			// false: Hide Note details
			$rootScope.$emit('toggleNoteDetail', false);
			// false: Hide Category details
			$rootScope.$emit('toggleCategoryDetail', true);
		});
	}

	$rootScope.$on('updateNumberOfNote', function (event, data){
		c.data.selectedItem.numberOfNotes = data;
	});

	$rootScope.$on('updateCategoryName', function (event, data){
		c.data.selectedItem.category_name = data.category_name;
		//c.server.refresh();
	});

	$rootScope.$on('refreshList', function (event){
		c.server.refresh();
	});

	spUtil.recordWatch($scope, "x_snc_createnotes_category", "numberSTARTSWITHCATE", function(name) {
		c.server.refresh();
	});
}

sp_widget.script:
(function() {

	data.categoryList = [];
	data.noteList = [];
	var categoryObj = {};

	var categoryList = new GlideRecord('x_snc_createnotes_category');
	//noteGR.addQuery('user', gs.getUser().getID());
	categoryList.orderByDesc('sys_created_on');
	//noteGR.setLimit(options.maximum_records_to_display);
	categoryList.query();
	while (categoryList.next()) {
		categoryObj = {};
		$sp.getRecordDisplayValues(categoryObj, categoryList, 'category_name, sys_id');

		// Note
		var count = 0;
		//var note = new GlideAggregate('x_snc_createnotes_qt_note');
		//note.addQuery('category', categoryObj.sys_id);
		//note.addAggregate('COUNT');
		//note.query();

		//if(note.next()) {
		//	count = note.getAggregate('COUNT');
		//}

		// Query
		var note2 = new GlideRecord("x_snc_createnotes_qt_note");
		note2.addQuery('category', categoryObj.sys_id);
		note2.query();
		// Get count
		count = note2.getRowCount();
		categoryObj.numberOfNotes = count;

		// Add to data
		data.categoryList.push(categoryObj);
	}

	if (input){
		if (input.action == 'getNoteList'){
			var noteRecord = new GlideRecord("x_snc_createnotes_qt_note");
			noteRecord.addQuery('category', input.categoryId);
			noteRecord.query();
			while (noteRecord.next()){
				var noteObj = {};
				$sp.getRecordDisplayValues(noteObj, noteRecord, 'note_title,note_details,category,sys_id');
				data.noteList.push(noteObj);
			}
		}
		if (input.action == 'getCategory'){
			data.categoryObj = {};
			var categoryRecord = new GlideRecord('x_snc_createnotes_category');
			categoryRecord.addQuery('sys_id', input.cateSysId);
			categoryRecord.query();
			while (categoryRecord.next()) {
				categoryObj = {};
				$sp.getRecordDisplayValues(categoryObj, categoryRecord, 'category_name, sys_id');
				data.categoryObj = categoryObj;
			}
		}
		if (input.action == 'newCategory'){
			var newCategory = new GlideRecord('x_snc_createnotes_category');
			newCategory.newRecord();
			newCategory.insert();

			data.newCategory = {};
			$sp.getRecordDisplayValues(data.newCategory, newCategory, 'sys_id, category_name');
		}
	}

}
)();
