sp_widget.client_script:
function($scope,$rootScope, spModal, spUtil) {
	/* widget controller */
	var c = this;
	// Init isShow
	c.data.isShow = false;

	$rootScope.$on('toggleCategoryDetail', function (event, data){
		c.data.isShow = data;
	});

	$rootScope.$on('setDetails', function (event, data){
		c.data.category = data;
	});

	c.updateCategory = function (){
		c.server.get({
			action : 'updateCategory',
			cateSysId : c.data.category.sys_id,
			cateName : c.data.category.category_name
		}).then(function(r){
			$rootScope.$emit('updateCategoryName', r.data.category);
		});
	}
	c.deleteCategory = function() {
		var cateName = c.data.category.category_name;
		c.server.get({
			action: 'deleteCategory',
			cateSysId : c.data.category.sys_id
		}).then(function(r) {
			c.data.category.category_name = '';
			$rootScope.$emit('refreshList');
			// Show message
			spUtil.addInfoMessage("The " + cateName + " category has been deleted.");
			c.data.isShow = false;
		});
	}
	c.confirmDelete = function(){
		spModal.confirm("Are you sure want to delete this Category?" + 
										"</br>" + 
										"All Notes in this Category will be lost!"
									 ).then(function(confirmed){
			if (confirmed){
				c.deleteCategory();
			}
		});
	}
}

sp_widget.script:
(function() {	

	if (input){
		data.category = {};

		// Get category
		var categoryRecord = new GlideRecord('x_snc_createnotes_category');
		categoryRecord.addQuery('sys_id', input.cateSysId);
		categoryRecord.query();

		if (input.action == 'updateCategory'){			
			while (categoryRecord.next()){
				categoryRecord.category_name = input.cateName;
				categoryRecord.update("Edit Category Name");
				$sp.getRecordDisplayValues(data.category, categoryRecord, 'category_name, sys_id');
			}
		}
		if (input.action == 'deleteCategory'){
			// Delete Notes
			var noteRecord = new GlideRecord('x_snc_createnotes_qt_note');
			noteRecord.addQuery('category', input.cateSysId);
			noteRecord.query();
			while (noteRecord.next()){
				noteRecord.deleteRecord();
			}

			// Delete Category
			while (categoryRecord.next()){
				categoryRecord.deleteRecord();
			}
		}
	}
})();
